ts').html(s);								$('#pager').pagination({					items: cnt,					itemsOnPage: 30,					cssStyle: 'light-theme',					currentPage: <?php echo $pageNo; ?>,					onPageClick: function(pageNumber, event) {						window.location = "<?php echo base_url('admin/index/amoodyWizard'); ?>/"+pageNumber;					}				});								$('.tree').click(function(){					var alltypes=requests[$(this).attr('resultJson')].result;					if(typeof flag[$(this).attr('resultJson')] == 'undefined')						flag[$(this).attr('resultJson')]=false;					if(!flag[$(this).attr('resultJson')])						$(this).css('direction','ltr').html('').jsontree(alltypes);					flag[$(this).attr('resultJson')]=true;				});			});						        </script>        <script type="text/javascript" src="<?php echo base_url('assets/js'); ?>/cpanel.js"></script>		<script type="text/javascript" src="<?php echo base_url('assets/js'); ?>/jquery.simplePagination.js"></script>		<script type="text/javascript" src="<?php echo base_url('assets/js'); ?>/jsontree.js"></script>           </body></html>